mapper.select:
SELECT * FROM sys_permission
WHERE id IN(
  SELECT sys_permission_id FROM sys_role_permission
  WHERE sys_role_id IN(
    SELECT sys_role_id FROM sys_user_role
    WHERE sys_user_id IN (
    SELECT usercode FROM sys_user WHERE usercode=#{value}
    )
  )
)
AND TYPE='menu'

SELECT * FROM sys_permission
WHERE id IN(
SELECT sys_permission_id FROM sys_role_permission
WHERE sys_role_id IN(
SELECT sys_role_id FROM sys_user_role
WHERE sys_user_id IN (
SELECT usercode FROM sys_user WHERE usercode=#{value}
)
)
)
AND TYPE='permission'
